import React, { Component } from "react";
import { Link } from "react-router-dom";
import ProjectDataService from "../../services/project.service";
import App from "../../App"
 
export default class Project extends Component {
constructor(props) {
    super(props);
    this.retrieveProjects = this.retrieveProjects.bind(this);
    this.state = {
      projects: []
    };
  }

  componentDidMount() { //Once component is in DOM
    this.retrieveProjects();
  }

  retrieveProjects() {
    ProjectDataService.getAll()
      .then(response => {
        this.setState({
          projects: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }


  // ----------------- JSX ---------------
  render() {
    const { projects } = this.state;
    return (
      <>
        <App />
        <div className="wrapper-project">
                     
          {projects && projects.map((project) => (

             <div className="item-project">

              {/* if is_granted('ROLE_ADMIN') 
              <a className="deleteProjectButton-project alertDisplayerDelete_JS" 
                href="{{ path('app_delete_project',{id: project.id}) }}"
                data-page="{{ path('app_delete_project',{id: project.id, _csrf_token: csrf_token('delete')}) }}"

              >Supprimer ce projet</a>
               endif */}
              

              <span className="title-project">{project.project_name}</span>

              <span className="tag-project">{project.project_technologies}</span>

              <img className="image-project" src=""></img>
       
              <Link className="link-project" to={"/Project/" + project.project_id}>See More</Link>
              
            </div>

          ))}

          {/* if is_granted('ROLE_ADMIN') %} */}
          <Link className="buttonAdd-project" to="/Project/create">Ajouter un nouveau projet</Link>

          
        </div>
      </>
    );
  }
}
