import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import ProjectDataService from "../../services/project.service";
import App from "../../App"

export default function FormProject(props) {

  //----------------------------REDIRECTION-----------------------
  const navigate = useNavigate();
  useEffect(() => { //si le composant a été updated d'une manière quelconque (state) alors ca s'execute
    if (project.project_is_created_or_updated) {
      navigate('/Projects');
    }
  });

  //----------------------------State------------------------------
  const [project,setProject] = useState( //déclare en tant que liste key/value
    { 
      project_id: null,
      project_name: "",
      project_technologies: "",
      project_description: "",
      project_thumbnail_filename: "",
      project_is_file_format: true,
      project_release_filename: "",
      project_release_url: "",
      project_is_created_or_updated: false,
    }
  );

  //if props = update alors ca
  // Call api pour changer le state
  //useEffect to modify form with component state

  // Event de modifications du formulaires
  const onChangeProject_name = (e) => {
    setProject(prevState => {
      return { ...prevState, project_name : e.target.value }
    });
  }

  const onChangeProject_technologies = (e) => {
    setProject(prevState => {
      return { ...prevState, project_technologies : e.target.value }
    });
  }

  const onChangeProject_description = (e) => {
    setProject(prevState => {
      return { ...prevState, project_description : e.target.value }
    });
  }

  const onChangeProject_thumbnail_filename = (e) => {
    setProject(prevState => {
      return { ...prevState, project_thumbnail_filename : e.target.value }
    });
  }

  const onChangeProject_is_file_format = (e) => {
    setProject(prevState => {
      return { ...prevState, project_is_file_format : e.target.value }
    });
  }

  const onChangeProject_release_filename = (e) => {
    setProject(prevState => {
      return { ...prevState, project_release_filename : e.target.value }
    });
  }

  const onChangeProject_release_url = (e) => {
    setProject(prevState => {
      return { ...prevState, project_release_url : e.target.value }
    });
  } 

  const saveProject = (e) => {
    let data = {
      project_name: project.project_name,
      project_technologies: project.project_technologies,
      project_description: project.project_description,
      project_thumbnail_filename: project.project_thumbnail_filename,
      project_is_file_format: project.project_is_file_format,
      project_release_filename: project.project_release_filename,
      project_release_url: project.project_release_url
    };
    ProjectDataService.create(data)
      .then(response => {
        setProject({
          project : {
            project_id : response.data.id,
            project_name: response.data.project_name,
            project_technologies: response.data.project_technologies,
            project_description: response.data.project_description,
            project_thumbnail_filename: response.data.project_thumbnail_filename,
            project_is_file_format: response.data.project_is_file_format,
            project_release_filename: response.data.project_release_filename,
            project_release_url: response.data.project_release_url
          }
        });
        console.log(response.data);
        setProject(prevState => {
          return { ...prevState, project_is_created_or_updated : true }
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  return (
    <>
      <App></App>
      <div className="wrapper-form">
        <div className="box-form">
       
          <div className="item-form">
            <div className="label-form">Nom du Projet</div>
            <input 
              type="text"
              className="textFields-form small" 
              id="project_name" 
              value={project.project_name} 
              onChange={onChangeProject_name} 
              name="project_name"
            />
          </div>

          <div className="item-form">
            <div className="label-form">Technologies sources et cibles</div>
            <input 
              type="text"
              className="textFields-form" 
              id="project_technologies"
              value={project.project_technologies} 
              onChange={onChangeProject_technologies} 
              name="project_technologies"
            /> 
          </div>

          <div className="item-form">
            <div className="label-form">Description</div>
            <textarea
              className="textArea-form" 
              id="project_description"
              value={project.project_description} 
              onChange={onChangeProject_description} 
              name="project_description">
            </textarea> 
          </div>

          <div className="item-form">
            <div className="label-form">Miniature du projet</div>
            {/* className='files-form' }
          </div>

          <div className="item-form">
            <div className="label-form">Images de présentation du projet</div>
            {/* className='files-form' */}
          </div> 

          <div className="item-form">
            <div className="label-form">Format</div>
            <select 
              className='FormatChoice_JS'
              id="project_is_file_format"  
              value={project.project_is_file_format} 
              onChange={onChangeProject_is_file_format} 
              name="project_is_file_format">
                <option value="true">File</option>
                <option value="false">Link</option>
            </select>
          </div>

          { project_is_file_format ?

            <div className="item-form fileInput_JS">
                <div className="label-form">Fichier</div>  
                {/* className='files-form' */}
            </div>

          : //url_format

            <div className="item-form urlInput_JS">
              <div className="label-form">Lien</div>
              <input 
                type="text"
                className="textFields-form" 
                id="project_release_url"
                value={project.project_release_url} 
                onChange={onChangeProject_release_url} 
                name="project_release_url"
              /> 
            </div>

          } 

          <button className="item-form button-form" onClick={saveProject}>Submit Project</button>
 
        </div>
    </div>
  </>
  );
}
