import React, { Component } from "react";
import { Link } from "react-router-dom";
import ProjectDataService from "../../services/project.service";
import App from "../../App"

export default class CreateProject extends Component {
  constructor(props) {
    super(props);

    // Event de modifications du formulaires
    this.onChangeProject_name = this.onChangeProject_name.bind(this);
    this.onChangeProject_technologies = this.onChangeProject_technologies.bind(this);
    this.onChangeProject_description = this.onChangeProject_description.bind(this);
    this.onChangeProject_thumbnail_filename = this.onChangeProject_thumbnail_filename.bind(this);
    this.onChangeProject_is_file_format = this.onChangeProject_is_file_format.bind(this);
    this.onChangeProject_release_filename = this.onChangeProject_release_filename.bind(this);
    this.onChangeProject_release_url = this.onChangeProject_release_url.bind(this);
    //this.onChange%Images% = this.onChange%Images%.bind(this);
    
    this.saveProject = this.saveProject.bind(this);

    this.state = {
      project_id : null,
      project_name: "",
      project_technologies: "",
      project_description: "",
      project_thumbnail_filename: "",
      project_is_file_format: true,
      project_release_filename: "",
      project_release_url: ""
    };
  }

  // Event de modifications du formulaires
  onChangeProject_name(e) {
    this.setState({
      project_name: e.target.value
    });
  }
  onChangeProject_technologies(e) {
    this.setState({
      project_technologies: e.target.value
    });
  }
  onChangeProject_description(e) {
    this.setState({
      project_description: e.target.value
    });
  }
  onChangeProject_thumbnail_filename(e) {
    this.setState({
      project_thumbnail_filename: e.target.value
    });
  }
  onChangeProject_is_file_format(e) {
    this.setState({
      project_is_file_format: e.target.value
    });
  }
  onChangeProject_release_filename(e) {
    this.setState({
      project_release_filename: e.target.value
    });
  }
  onChangeProject_release_url(e) {
    this.setState({
      project_release_url: e.target.value
    });
  }

  saveProject() {
    let data = {
      project_id : this.state.project_id,
      project_name: this.state.project_name,
      project_technologies: this.state.project_technologies,
      project_description: this.state.project_description,
      project_thumbnail_filename: this.state.project_thumbnail_filename,
      project_is_file_format: this.state.project_is_file_format,
      project_release_filename: this.state.project_release_filename,
      project_release_url: this.state.project_release_url
    };
    ProjectDataService.create(data)
      .then(response => {
        this.setState({
          project_id : response.data.id,
          project_name: response.data.project_name,
          project_technologies: response.data.project_technologies,
          project_description: response.data.project_description,
          project_thumbnail_filename: response.data.project_thumbnail_filename,
          project_is_file_format: response.data.project_is_file_format,
          project_release_filename: response.data.project_release_filename,
          project_release_url: response.data.project_release_url
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    return (
      <>
        <App></App>
        <div className="wrapper-form">
          <div className="box-form">
         
            <div className="item-form">
              <div className="label-form">Nom du Projet</div>
              <input 
                type="text"
                className="textFields-form small" 
                id="project_name"
                required 
                value={this.state.project_name} 
                onChange={this.onChangeProject_name} 
                name="project_name"
              />
            </div>

            <div className="item-form">
              <div className="label-form">Technologies sources et cibles</div>
              <input 
                type="text"
                className="textFields-form" 
                id="project_technologies"
                required 
                value={this.state.project_technologies} 
                onChange={this.onChangeProject_technologies} 
                name="project_technologies"
              /> 
            </div>

            <div className="item-form">
              <div className="label-form">Description</div>
              <textarea
                className="textArea-form" 
                id="project_description"
                required 
                value={this.state.project_description} 
                onChange={this.onChangeProject_description} 
                name="project_description">
              </textarea> 
            </div>

            <div className="item-form">
              <div className="label-form">Miniature du projet</div>
              {/* className='files-form' */}
            </div>

            <div className="item-form">
              <div className="label-form">Images de présentation du projet</div>
              {/* className='files-form' */}
            </div> 

            <div className="item-form">
              <div className="label-form">Format</div>
              <select 
                className='FormatChoice_JS'
                id="project_is_file_format"
                required 
                value={this.state.project_is_file_format} 
                onChange={this.onChangeProject_is_file_format} 
                name="project_is_file_format">
                  <option value="true">File</option>
                  <option value="false">Link</option>
              </select>
            </div>

            <div className="item-form fileInput_JS">
                <div className="label-form">Fichier</div>  
                {/* className='files-form' */}
            </div>

            <div className="item-form urlInput_JS">
              <div className="label-form">Lien</div>
              <input 
                type="text"
                className="textFields-form" 
                id="project_release_url"
                required 
                value={this.state.project_release_url} 
                onChange={this.onChangeProject_release_url} 
                name="project_release_url"
              /> 
            </div>
            
            <Link className="item-form button-form" to="/Projects" onClick={this.saveProject}>Submit Project</Link>
            {/* formnovalidate pour ne pas avoir la validation HTMML 5 */}
   
          </div>
      </div>
    </>
    );
  }
}
