import React, { useState, useEffect } from 'react';
import { Link , useParams} from "react-router-dom";
import ProjectDataService from "../../services/project.service";
import App from "../../App"


class GetOneProject extends Component {
  constructor(props) {
    super(props);
    this.getProject = this.getProject.bind(this);
    this.deleteProject = this.deleteProject.bind(this);
    this.state = {
      project: {
        project_id: null,
        project_name: "",
        project_technologies: "",
        project_description: "",
        project_thumbnail_filename: "",
        project_is_file_format: true,
        project_release_filename: "",
        project_release_url: "" 
      }
    };
  }

  componentDidMount() {
    this.getProject(this.props.match.params.id); //récupère le paramètre id dans la requete
  }

  getProject(id) {
    ProjectDataService.get(id)
      .then(response => {
        this.setState({
          project: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }
  

  deleteProject() {    
    ProjectDataService.delete(this.state.project.project_id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/Projects')
      })
      .catch(e => {
        console.log(e);
      });
  }


  render() {
    const { project } = this.state;
    return (
      <>
        <App />
        <div className="wrapper-discover">
          <div className="box-discover">

            {/* if is_granted('ROLE_ADMIN') %} 
            <a className="item-discover deleteProjectButton-project alertDisplayerDelete_JS" 
              href="{{ path('app_delete_project',{id: project.id}) }}"
              data-page="{{ path('app_delete_project',{id: project.id, _csrf_token: csrf_token('delete')}) }}" 
            >Supprimer ce projet</a>
            {% endif %}*/}
            
            <span className="item-discover title-discover">{ project.project_name }</span>

            <span className="item-discover tag-discover">{ project.project_technologies }</span>  

            <span className="item-discover description-discover">{ project.project_description }</span>

            {/* for image in project.project_images 
              <img className="item-discover image-discover caroussel_JS" src="{{ uploaded_asset(image.ImageFileName) }}"></img>
            {% endfor */}


            {/* if project.isFileFormat == true %} 
              <a className="item-discover link-project" href="{{ uploaded_asset(project.ProjectFileName) }}" download>Télécharger</a>
            {% else %}
              <a className="item-discover link-project" href="{{ project.urlProject }}" download>Lien</a>
            {% endif */}  

          </div>
      </div>
      </>
    );
  }
}

export default withRouter(GetOneProject);
